var cy_email = "lqir#klssrjudsk#lr";

function decodeEmail(encodedString) {
    const shift = 3; // The shift used in the cipher
    let decodedEmail = '';
    let hashCount = 0; // Counter for '#' occurrences

    for (let i = 0; i < encodedString.length; i++) {
        let charCode = encodedString.charCodeAt(i);

        if ((charCode >= 65 && charCode <= 90) || (charCode >= 97 && charCode <= 122)) {
            // Check if character is uppercase (65-90) or lowercase (97-122)
            let base = charCode < 97 ? 65 : 97;
            charCode = ((charCode - base - shift + 26) % 26) + base; // Decode and wrap around the alphabet
        } else if (charCode === 35) { // '#' character
            hashCount++;
            if (hashCount === 1) {
                charCode = 64; // Replace first '#' with '@'
            } else if (hashCount === 2) {
                charCode = 46; // Replace second '#' with '.'
            }
        }

        decodedEmail += String.fromCharCode(charCode);
    }

    return decodedEmail;
}

function createMailtoLink(email, subject) {
    const encodedSubject = encodeURIComponent(subject);
    return `mailto:${email}?subject=${encodedSubject}`;
}

const decodedEmail = decodeEmail(cy_email);
const decodedLink = createMailtoLink(decodedEmail, 'Question for you');

Outputs.email = decodedEmail;
Outputs.email_link = decodedLink;

